class BookShelf extends React.Component {
  constructor(props) {
    super(props);
    this.props = props;
    this.state = {
      books: []
    }
  }

  componentDidMount() {
    // Books taken from http://www.gutenberg.org/browse/scores/top
    this.setState({
      books: [
        {
          id: 1,
          title: 'Pride and Prejudice',
          author: 'Jane Austen',
          pages: 432,
          current: 1,
          status: 'Unread',
          description: 'Pride and Prejudice is an 1813 romantic novel by Jane Austen. It charts the emotional development of the protagonist Elizabeth Bennet, who learns the error of making hasty judgments and comes to appreciate the difference between the superficial and the essential.',
          cover: 'data:image/webp;base64,'
        },
        {
          id: 2,
          title: 'Frankenstein; Or, The Modern Prometheus',
          author: 'Mary Wollstonecraft Shelley',
          pages: 280,
          current: 120,
          status: 'Reading',
          description: 'Frankenstein; or, The Modern Prometheus is a novel written by English author Mary Shelley that tells the story of Victor Frankenstein, a young scientist who creates a hideous, sapient creature in an unorthodox scientific experiment.',
          cover: 'data:image/webp;base64,'
        },
        {
          id: 3,
          title: 'A Tale of Two Cities',
          author: 'Charles Dickens',
          pages: 341,
          current: 341,
          status: 'Complete',
          description: 'A Tale of Two Cities is a historical novel by Charles Dickens, set in London and Paris before and during the French Revolution.',
          cover: 'data:image/webp;base64,'
        }
      ]
    });
  }

  render() {
    return <Container>
      <ul className="list-unstyled">
      {
        this.state.books.map((book, idx) => {
          return <React.Fragment key={idx}>
            <hr/>
            <Media as="li">
              <img width={64} className="mr-3" src={book.cover} />
              <Media.Body>
                <h5>{book.title}</h5> 
                <p>{book.description}</p>
              </Media.Body>
            </Media>

            <hr/>
          </React.Fragment>
        })
      }
      </ul>
    </Container>;
  }
}

