class Navigation extends React.Component {
  constructor(props) {
    super(props);
    this.props = props;
  }

  randomBook() {
    return 1; // TODO: actually generate a random book id.
  }

  // updateParentLogin() {
  //   this.props.toggleLoginDetails
  // }

  render() {
    let navbar_login = (this.props.loggedin) ? 
    <React.Fragment>
    <Nav.Link href="/" disabled>{'Logged in as ' + this.props.user + ' '}</Nav.Link> 
    <Nav.Link href="/settings">Settings</Nav.Link>
    <Nav.Link href="/" onClick={this.props.toggleLoginDetails}>Logout</Nav.Link>
    </React.Fragment> :
    <Nav.Link href="/">Login</Nav.Link>;

    return (
      <Navbar bg="dark" variant="dark">
      <Navbar.Brand href="/">GoRead</Navbar.Brand>
      <Nav className="mr-auto">
        <Nav.Link href="/shelf">Book Shelf</Nav.Link>
        <Nav.Link href={"/reader/" + this.randomBook()}>Random Book</Nav.Link>
      </Nav>
      <Nav className="justify-content-end">
      {navbar_login}
      </Nav>
    </Navbar>);
  }
}

